import { render, screen } from '@testing-library/react';

import { PdfViewerDialog } from '..';

describe('PdfViewerDialog', () => {
  const defaultProps = {
    open: true,
    title: 'PDF Document',
    url: 'https://example.com/sample.pdf',
    onClose: jest.fn(),
    dataTestId: 'test-pdf-viewer-dialog',
  };

  beforeEach(() => {
    jest.clearAllMocks();
  });

  it('renders the dialog with URL when open', () => {
    render(<PdfViewerDialog {...defaultProps} />);

    // Check that the dialog content is rendered
    expect(screen.getByTestId('test-pdf-viewer-dialog-title')).toBeInTheDocument();
    expect(screen.getByTestId('test-pdf-viewer-dialog-title')).toHaveTextContent('PDF Document');

    // Check that the embed element is rendered with the correct URL
    const embedElement = screen.getByTestId('test-pdf-viewer-dialog-embed') as HTMLEmbedElement;
    expect(embedElement).toBeInTheDocument();
    expect(embedElement.src).toContain('https://example.com/sample.pdf');
    expect(embedElement.type).toBe('application/pdf');
  });

  it('renders the dialog with base64 data when provided', () => {
    const base64Props = {
      ...defaultProps,
      url: undefined,
      data: '',
    };
    render(<PdfViewerDialog {...base64Props} />);

    // Check that the dialog content is rendered
    expect(screen.getByTestId('test-pdf-viewer-dialog-title')).toBeInTheDocument();
    expect(screen.getByTestId('test-pdf-viewer-dialog-title')).toHaveTextContent('PDF Document');

    // Check that the embed element is rendered with the correct base64 data
    const embedElement = screen.getByTestId('test-pdf-viewer-dialog-embed') as HTMLEmbedElement;
    expect(embedElement).toBeInTheDocument();
    expect(embedElement.src).toContain('data:application/pdf;base64,');
    expect(embedElement.type).toBe('application/pdf');
  });

  it('does not render the dialog when closed', () => {
    render(<PdfViewerDialog {...defaultProps} open={false} />);

    // Check that the dialog content is not rendered
    expect(screen.queryByTestId('test-pdf-viewer-dialog-content')).not.toBeInTheDocument();
    expect(screen.queryByTestId('test-pdf-viewer-dialog-embed')).not.toBeInTheDocument();
  });

  it('does not render anything when neither url nor data is provided', () => {
    const noDataProps = {
      ...defaultProps,
      url: undefined,
      data: undefined,
    };
    const { container } = render(<PdfViewerDialog {...noDataProps} />);

    // Check that nothing is rendered
    expect(container.firstChild).toBeNull();
  });

  it('renders dialog with ReactNode title', () => {
    const customTitle = (
      <div>
        <span data-testid="custom-title-part">PDF</span> Viewer
      </div>
    );

    render(<PdfViewerDialog {...defaultProps} title={customTitle} />);

    // Check that the custom title is rendered correctly
    expect(screen.getByTestId('custom-title-part')).toBeInTheDocument();
    expect(screen.getByText('Viewer')).toBeInTheDocument();
  });

  it('renders the dialog with fullscreen dimensions', () => {
    render(<PdfViewerDialog {...defaultProps} />);

    // Check that the dialog has appropriate classes for fullscreen viewing
    const dialogContent = screen.getByTestId('test-pdf-viewer-dialog-content');
    expect(dialogContent).toHaveClass('h-[95dvh]');
    expect(dialogContent).toHaveClass('max-w-[95dvw]');
  });

  it('uses custom dataTestId when provided', () => {
    const customDataTestId = 'custom-pdf-viewer';

    render(<PdfViewerDialog {...defaultProps} dataTestId={customDataTestId} />);

    // Check that custom dataTestId is used
    expect(screen.getByTestId(`${customDataTestId}-content`)).toBeInTheDocument();
    expect(screen.getByTestId(`${customDataTestId}-title`)).toBeInTheDocument();
    expect(screen.getByTestId(`${customDataTestId}-embed`)).toBeInTheDocument();
  });
});
